  </button>
  <span v-show="isNama"> YES</span>
  <span v-show="!isNama"> NO</span><br />
  <button type="button" class="btn btn-primary" @click="clickDraw">開始</button
  ><br />

  <GBowl :x="adjust_rotate_g_x" :y="adjust_rotate_g_y" :draw="draw" />
</template>

