    const adjust_g_x = ref(0)
    const adjust_g_y = ref(0)
    let before_g_x = 0
    let before_g_y = 0
    let angle_g_xy = 0

    const gyro_x = ref(0)
    const gyro_y = ref(0)
    const gyro_z = ref(0)

    let before_x = 0
    let before_y = 0
    let before_z = 0
    let after_x = 0
    let after_y = 0
    let after_z = 0

    const isCalibrated1 = ref(false)
    const isCalibrated2 = ref(false)

    const isNama = ref(false)

    // 車の加速度を計測する際の角度を計測する
    const cickCalibration1 = () => {
      before_x = gyro_x.value
      before_y = gyro_y.value
      before_z = gyro_z.value
      isCalibrated1.value = true
      alert(
        `angle(角度) \n
        x: ${before_x} y:${before_y} z:${before_z} \n
        `
      )
    }

    // スマホを固定しときの角度を記憶する
    const cickCalibration2 = () => {
      after_x = gyro_x.value
      after_y = gyro_y.value
      after_z = gyro_z.value
      isCalibrated2.value = true
      alert(
        `angle(角度) \n
        before x: ${before_x} y:${before_y} z:${before_z} \n
        after x: ${after_x} y:${after_y} z:${after_z} \n
        `
      )
    }

    const gyro_result = () => {
      alert(
        `x:${gyro_x.value - before_x} y:${gyro_y.value - before_y} z:${
          gyro_z.value - before_z
        }`
      )
    }

    //// 音声
    // const base64_voice_01 =
    //   'data:audio/mp3;base64,'
    // const base64_voice_02 =
    //   'data:audio/mp3;base64,'
    // const base64_voice_03 =
    //   'data:audio/mp3;base64,'
    // const base64_voice_04 =
    //   'data:audio/mp3;base64,'
    // const base64_voice_05 =
    //   'data:audio/mp3;base64,'
    // const base64_voice_06 =
    //   'data:audio/mp3;base64,'
    // const base64_voice_07 =
    //   'data:audio/mp3;base64,'
    // const base64_voice_08 =
    //   'data:audio/mp3;base64,'
    // const base64_voice_09 =
    //   'data:audio/mp3;base64,'
    // const base64_voice_10 =
    //   'data:audio/mp3;base64,'
    // const base64_voice_11 =
    //   'data:audio/mp3;base64,'
    // const base64_voice_12 =
    //   'data:audio/mp3;base64,'
    // const base64_voice_13 =
    //   'data:audio/mp3;base64,'
    // const base64_voice_14 =
    //   'data:audio/mp3;base64,'
    // const base64_voice_15 =
    //   'data:audio/mp3;base64,'
    // const base64_voice_16 =
    //   'data:audio/mp3;base64,'
    // const base64_voice_17 =
    //   'data:audio/mp3;base64,'
    // const base64_voice_18 =
    //   'data:audio/mp3;base64,'

    // gサークルを描画するかどうかのフラグ
    const draw = ref(false)

    let before_rotate_g_x = 0
    let before_rotate_g_y = 0
    const adjust_rotate_g_x = ref(0)
    const adjust_rotate_g_y = ref(0)
    // 回転後の加速度の値
    const rotate_acceleration_x = ref(0)
    const rotate_acceleration_y = ref(0)
    const rotate_acceleration_z = ref(0)

    // 加速度センサーから値が取得できた時に呼ばれるイベント処理
    const deviceAcceleration = (e: DeviceMotionEvent) => {
      // 加速度センサーが有効になっているかどうかのチェック
      if (!useAccelerationSensor.stateRefs.isEnable.value) return
      // 加速度取得
      const acceleration = e.accelerationIncludingGravity
      if (acceleration === null) return

      // const startTime = Date.now()
      const e_acceleration_x = acceleration.x ?? 0
      const e_acceleration_y = acceleration.y ?? 0
      const e_acceleration_z = acceleration.z ?? 0

      // 加速度をGに変換
      const g_x = e_acceleration_x / 9.8
      const g_y = e_acceleration_y / 9.8

      // ローパスフィルタでのノイズ削除
      adjust_g_x.value = useAccelerationSensor.filter(before_g_x, g_x).LPF()
      adjust_g_y.value = useAccelerationSensor.filter(before_g_y, g_y).LPF()
      before_g_x = g_x
      before_g_y = g_y

      // Gの角度を算出
      angle_g_xy = pointToAtan2({ x: g_x, y: g_y }) + 3

      // 回転
      const angle_x = after_x - before_x
      const angle_y = after_y - before_y
      const angle_z = Math.abs(before_z - after_z)
      let rotate_acceration = null
      if (!isNama.value) {
        rotate_acceration = rotate3dVector(
          e_acceleration_x,
          e_acceleration_y,
          e_acceleration_z,
          angle_x,
          angle_y,
          angle_z
        )
      } else {
        rotate_acceration = {
          x: e_acceleration_x,
          y: e_acceleration_y,
          z: e_acceleration_z,
        }
      }

      // 速度をGに変換
      const rotate_g_x = rotate_acceration.x / 9.8
      const rotate_g_y = rotate_acceration.y / 9.8

      // ローパスフィルタでのノイズ削除
      adjust_rotate_g_x.value = useAccelerationSensor
        .filter(before_rotate_g_x, rotate_g_x)
        .LPF()
      adjust_rotate_g_y.value = useAccelerationSensor
        .filter(before_rotate_g_y, rotate_g_y)
        .LPF()
      before_rotate_g_x = rotate_g_x
      before_rotate_g_y = rotate_g_y

      rotate_acceleration_x.value = rotate_acceration.x
      rotate_acceleration_y.value = rotate_acceration.y
      rotate_acceleration_z.value = rotate_acceration.z

      // console.log(rotate_acceration.z)
      // if(isCalibrated2.value){
      //   alert(rotate_acceration.z)
      // }
    }

    // ジャイロセンサーから値が取得できた時に呼ばれるイベント処理
    const deviceGyro = (e: DeviceOrientationEvent) => {
      // ジャイロセンサーが有効になっているかどうかのチェック
      if (!useGyroSensor.stateRefs.isEnable.value) return
      if (e.alpha === null || e.beta === null || e.gamma === null) return

      gyro_z.value = e.alpha
      gyro_y.value = e.gamma
      gyro_x.value = e.beta

      if (before_x == 0 && before_y == 0 && before_z == 0) {
        before_x = gyro_x.value
        before_y = gyro_y.value
        before_z = gyro_z.value
      }
    }

    /**
     * 3次元ベクトルの回転を行う。
     *
     * @param number $vector_x
     * @param number $vector_y
     * @param number $vector_z
     * @param number $angle_x
     * @param number $angle_y
     * @param number $angle_z
     * @return {x:number, y:number, z:number}
     */
    const rotate3dVector = (
      vector_x: number,
      vector_y: number,
      vector_z: number,
      angle_x: number,
      angle_y: number,
      angle_z: number
    ): {x:number, y:number, z:number}=> {
      // ３次元回転行列の公式が右回りなのでマイナス角度の場合は変換処理を挟む。
      // z軸は0-360度なので変換は不要。
      if (angle_x < 0) {
        angle_x = 360 + angle_x
      }
      if (angle_y < 0) {
        angle_y = 360 + angle_y
      }

      // 角度→ラジアンに変換
      const razian_x = angle_x * (Math.PI/ 180)
      const razian_y = angle_y * (Math.PI/ 180)
      const razian_z = angle_z * (Math.PI/ 180)

      // x軸周りに右回転した座標を取得する表現行列
      const matrix_x = [
        [1, 0, 0],
        [0, Math.cos(razian_x), -Math.sin(razian_x)],
        [0, Math.sin(razian_x), Math.cos(razian_x)],
      ]

      // // y軸周り右回転した座標を取得する表現行列
      const matrix_y = [
        [Math.cos(razian_y), 0, Math.sin(razian_y)],
        [0, 1, 0],
        [-Math.sin(razian_y), 0, Math.cos(razian_y)],
      ]

      // z軸周りに右回転した座標を取得する表現行列
      const matrix_z = [
        [Math.cos(razian_z), -Math.sin(razian_z), 0],
        [Math.sin(razian_z), Math.cos(razian_z), 0],
        [0, 0, 1],
      ]

      /**
       * 回転行列を使ってベクトルの回転を行う。
       *
       * @param number[][] matrix
       * @param number[] vector
       * @return {x:number, y:number, z:number}
       */
      const calc = (
        matrix: number[][],
        vector: number[]
      ): {x:number, y:number, z:number} => {
        return {
          x:
            matrix[0][0] * vector[0] +
            matrix[0][1] * vector[1] +
            matrix[0][2] * vector[2],
          y:
            matrix[1][0] * vector[0] +
            matrix[1][1] * vector[1] +
            matrix[1][2] * vector[2],
          z:
            matrix[2][0] * vector[0] +
            matrix[2][1] * vector[1] +
            matrix[2][2] * vector[2],
        }
      }

      // x軸回りの回転
      let rotational_vector = calc(matrix_x, [vector_x, vector_y, vector_z])
      // y軸回りの回転
      rotational_vector = calc(matrix_y, [rotational_vector.x, rotational_vector.y, rotational_vector.z])
      // z軸回りの回転
      rotational_vector = calc(matrix_z, [rotational_vector.x, rotational_vector.y, rotational_vector.z])

      return { x: rotational_vector.x, y: rotational_vector.y, z: rotational_vector.z }
    }

    // 「センサーを有効」押下
    const clickStartSensor = () => {
      // 加速度センサーの有効化
      useAccelerationSensor.addEvent(deviceAcceleration)
      useAccelerationSensor.enableSensor()
      // ジャイロセンサーの有効化
      useGyroSensor.addEvent(deviceGyro)
      useGyroSensor.enableSensor()
      alert('センサーを有効にしました。')
    }

    // 「作図」押下
    const clickDraw = () => {
      draw.value = true
      // const voice_01 = new Audio(base64_voice_01)
      // const voice_02 = new Audio(base64_voice_02)
      // const voice_03 = new Audio(base64_voice_03)
      // const voice_04 = new Audio(base64_voice_04)
      // const voice_05 = new Audio(base64_voice_05)
      // const voice_06 = new Audio(base64_voice_06)
      // const voice_07 = new Audio(base64_voice_07)
      // const voice_08 = new Audio(base64_voice_08)
      // const voice_09 = new Audio(base64_voice_09)
      // const voice_10 = new Audio(base64_voice_10)
      // const voice_11 = new Audio(base64_voice_11)
      // const voice_12 = new Audio(base64_voice_12)
      // const voice_13 = new Audio(base64_voice_13)
      // const voice_14 = new Audio(base64_voice_14)
      // const voice_15 = new Audio(base64_voice_15)
      // const voice_16 = new Audio(base64_voice_16)
      // const voice_17 = new Audio(base64_voice_17)
      // const voice_18 = new Audio(base64_voice_18)

      // // 音声出力に関する処理
      // setInterval(() => {
      //   const g_xy =
      //     Math.ceil(
      //       Math.sqrt(
      //         adjust_rotate_g_x.value ** 2 + adjust_rotate_g_y.value ** 2
      //       ) * 10
      //     ) / 10

      //   const round_g_xy = Math.round(g_xy * 10) / 10
      //   // const angle_time = Math.round(Math.round(angle_g_xy.value / 30))

      //   switch (round_g_xy) {
      //     case 0.1:
      //       return
      //     case 0.2:
      //       voice_02.play()
      //       break
      //     case 0.3:
      //       voice_03.play()
      //       break
      //     case 0.4:
      //       voice_04.play()
      //       break
      //     case 0.5:
      //       voice_05.play()
      //       break
      //     case 0.6:
      //       voice_06.play()
      //       break
      //     case 0.7:
      //       voice_07.play()
      //       break
      //     case 0.8:
      //       voice_08.play()
      //       break
      //     case 0.9:
      //       voice_09.play()
      //       break
      //     case 1:
      //       voice_10.play()
      //       break
      //     case 1.1:
      //       voice_11.play()
      //       break
      //     case 1.2:
      //       voice_12.play()
      //       break
      //     case 1.3:
      //       voice_13.play()
      //       break
      //     case 1.4:
      //       voice_14.play()
      //       break
      //     case 1.5:
      //       voice_15.play()
      //       break
      //     case 1.6:
      //       voice_16.play()
      //       break
      //     case 1.7:
      //       voice_17.play()
      //       break
      //     case 1.8:
      //       voice_18.play()
      //       break
      //   }
      // }, 500)
    }

    const clickDef = () => {
      alert(`x before:${before_x} after:${after_x}
            y before:${before_y} after:${after_y}
            z before:${before_z} after:${after_z}
      `)
    }

    const clickNamSensor = () => {
      isNama.value = !isNama.value
    }

    return {
      clickNamSensor,
      clickDraw,
      clickStartSensor,
      router,
      draw,
      adjust_g_x,
      adjust_g_y,
      angle_g_xy,
      gyro_z,
      gyro_y,
      gyro_x,
      cickCalibration1,
      cickCalibration2,
      gyro_result,
      rotate_acceleration_x,
      rotate_acceleration_y,
      rotate_acceleration_z,
      adjust_rotate_g_x,
      adjust_rotate_g_y,
      clickDef,
      isCalibrated1,
      isCalibrated2,
      isNama,
    }
  },
  computed: {},
